import React, { useState, useEffect } from 'react';
import {
  View,
  Text,
  Button,
  Modal,
  TextInput,
  FlatList,
  TouchableOpacity,
  StyleSheet,
} from 'react-native';
import AsyncStorage from '@react-native-async-storage/async-storage';
import { NavigationContainer } from '@react-navigation/native';
import { createNativeStackNavigator } from '@react-navigation/native-stack';

const Stack = createNativeStackNavigator();

const App = () => {
  const [tasks, setTasks] = useState([]);
  const [modalVisible, setModalVisible] = useState(false);
  const [taskName, setTaskName] = useState('');
  const [taskDescription, setTaskDescription] = useState('');

  const loadTasks = async () => {
    try {
      const storedTasks = await AsyncStorage.getItem('tasks');
      if (storedTasks !== null) {
        setTasks(JSON.parse(storedTasks));
      }
    } catch (error) {
      console.error(error);
    }
  };

  const saveTasks = async () => {
    try {
      await AsyncStorage.setItem('tasks', JSON.stringify(tasks));
    } catch (error) {
      console.error(error);
    }
  };

  useEffect(() => {
    loadTasks();
  }, []);

  useEffect(() => {
    saveTasks();
  }, [tasks]);

  const addTask = () => {
    if (taskName) {
      setTasks([...tasks, { name: taskName, description: taskDescription, completed: false }]);
      setTaskName('');
      setTaskDescription('');
      setModalVisible(false);
    }
  };

  const markTaskAsCompleted = (index) => {
    const updatedTasks = [...tasks];
    updatedTasks[index].completed = !updatedTasks[index].completed;
    setTasks(updatedTasks);
  };

  const deleteTask = (index) => {
    const updatedTasks = [...tasks];
    updatedTasks.splice(index, 1);
    setTasks(updatedTasks);
  };

  return (
    <View style={styles.container}>
      <Text style={styles.header}>Lista de Tareas</Text>
      <Button title="Agregar Tarea" onPress={() => setModalVisible(true)} />
      <FlatList
        data={tasks}
        renderItem={({ item, index }) => (
          <TouchableOpacity
            onPress={() => markTaskAsCompleted(index)}
            onLongPress={() => deleteTask(index)}
          >
            <View style={styles.task}>
              <Text style={item.completed ? styles.completedTask : styles.taskText}>{item.name}</Text>
              <Text style={styles.taskDescription}>{item.description}</Text>
            </View>
          </TouchableOpacity>
        )}
        keyExtractor={(item, index) => index.toString()}
      />
      <Modal visible={modalVisible} animationType="slide">
        <View style={styles.modalContainer}>
          <TextInput
            placeholder="Nombre de la tarea"
            value={taskName}
            onChangeText={(text) => setTaskName(text)}
            style={styles.input}
          />
          <TextInput
            placeholder="DescripciÃ³n de la tarea"
            value={taskDescription}
            onChangeText={(text) => setTaskDescription(text)}
            style={styles.input}
          />
          <Button title="Agregar" onPress={addTask} />
          <Button title="Cancelar" onPress={() => setModalVisible(false)} />
        </View>
      </Modal>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    padding: 16,
  },
  header: {
    fontSize: 24,
    fontWeight: 'bold',
    marginBottom: 16,
  },
  task: {
    backgroundColor: 'white',
    padding: 16,
    marginBottom: 8,
    borderWidth: 1,
    borderColor: '#ddd',
  },
  completedTask: {
    textDecorationLine: 'line-through',
  },
  taskText: {
    fontSize: 18,
  },
  taskDescription: {
    fontSize: 14,
    color: '#777',
  },
  modalContainer: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
  },
  input: {
    borderBottomWidth: 1,
    borderColor: 'gray',
    marginBottom: 16,
    fontSize: 16,
    width: '80%',
  },
});

const ToDoAppScreen = () => {
  return (
    <NavigationContainer>
      <Stack.Navigator>
        <Stack.Screen name="Tareas Pendientes" component={App} />
      </Stack.Navigator>
    </NavigationContainer>
  );
};

export default ToDoAppScreen;
